/*显示龙轩导航*/
var islxtools = 1;




var Config = fetch('hiker://files/Config.json', {})
if (Config != null && Config.length > 0) {
    eval('var obj=' + Config);
    for (var k in obj) {
        if (k == 'userwallpaperurl' || k == 'hitokototype' || k == 'jbturl')
            eval('var ' + k + ' = \'' + obj[k] + '\'')
        else
            eval('var ' + k + ' = ' + obj[k])
    }
}

var rule = JSON.parse(getRule());
var version = 7.7;
var json = {};
var res = {};
var d = [];
var allurl = '';
var remotelyurl = 'https://gitee.com/qingbailingyan/impurity/raw/master/homecontent.json';
var fileurl = 'hiker://files/homecontent.json';
var isfilehomecontent = false;
if (fetch(fileurl, {}) != null && fetch(fileurl, {}).length > 0) isfilehomecontent = true;
if (rule.firstHeader == "class") allurl = rule.class_url;
if (rule.firstHeader == "area") allurl = rule.area_url;
if (rule.firstHeader == "year") allurl = rule.year_url;
/*主页*/
