                    eval('json=' + Tujian);
                    wallpaperurl = json[0].local_url + '?type=.jpg';
                }
                break;
            case 8:
                var Tujian = fetch('https://v2.api.dailypics.cn/random?op=pc', {});
                if (Tujian != null && Tujian.length > 0) {
                    eval('json=' + Tujian);
                    wallpaperurl = json[0].local_url + '?type=.jpg';
                }
                break;
        }
    var hitokototitle = '';
    var hitokotodesc = '';
    var hitokotourl = '';
    switch (hitokotovalue) {
        case 0:
            break;
        case 1:
            var hitokoto = fetch('https://v1.hitokoto.cn/?c=' + hitokototype, {})
            if (hitokoto != null && hitokoto.length > 0) {
                eval('json=' + hitokoto);
                hitokototitle = json.hitokoto;
                hitokotodesc = '出处：\t《' + json.from + '》\n原作者：\t' + json.from_who;
                hitokotourl = 'https://hitokoto.cn/?id=' + json.id;
            }
            break;
    }
    if (wallpaperurl == '' && hitokotovalue != 0) {
        d.push({
            title: hitokototitle,
            col_type: 'text_1',
            desc: hitokotodesc,
            url: hitokotourl
        });
    }
    if (wallpaperurl != '') {
        if (istimestamp == 0)
            d.push({
                title: hitokototitle,
                col_type: 'pic_1',
                pic_url: wallpaperurl,
                desc: hitokotodesc,
                url: wallpaperurl
            });
