                col_type: 'text_1',
                url: 'http://haikuoshijie.cn/topic/190'
            });
            d.push({
                title: '\n📎 更新日志 📎\n',
                col_type: 'text_1'
            });
            var Uversionlog = fetch('https://gitee.com/qingbailingyan/impurity/raw/master/version_V' + Uversion + '.txt', {});
            for (var i = 0; i < Uversionlog.split('\n').length; i++) {
                d.push({
                    title: Uversionlog.split('\n')[i],
                    col_type: 'text_1'
                });
            }
        }
    }
}
/*关于*/
if (allurl == "关于") {
    var u = fetch('https://gitee.com/qingbailingyan/impurity/raw/master/version.txt', {});
