js: var rss = new XML('<rss' + getResCode().split('<rss')[1]);
var json = JSON.parse(getRule());
var res = {};
var d = [];
var items = rss.child(0).children();
for (var i in items) {
    if ((items[i] + '').indexOf('<item') == 0) {
        var r = {};
        var j = items[i];
        r.title = j.child(0) + '';
        var p = (j.child(1) + '').match(/src="(.*?)"/);
        if (p != null) {
            r.pic_url = p[1]
        } else {
            r.pic_url = '*'
        };
        r.desc = (j.child(1) + '').replace(/<.*?>/ig, '').replace('\n', ' ');
        if (r.title[0] == r.desc[0]) {
            r.desc = j.child(5) + ' | ' + j.child(2)
        };
        if (json.class_url == 'telegram/channel/lovesource' && (j.child(1) + '').indexOf('href') != -1)
            r.url = (j.child(1) + '').match(/href="(.*?)"/)[1];
        else
            r.url = j.child(4);
        d.push(r);
    }
}
res.data = d;
setHomeResult(res);

